import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/BotArmy';
import BotSpecs from './components/BotSpecs';
import SortBar from './components/SortBar'; 
import './App.css';

const App = () => {
  const [bots, setBots] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);
  const [setSortedBy] = useState(null);

  useEffect(() => {
    // Fetch data 
    fetch('http://localhost:3001/bots')
      .then((response) => response.json())
      .then((data) => setBots(data));
  }, []);

  const enlistBot = (bot) => {
    // Add bot to enlistedBots
    setEnlistedBots([...enlistedBots, bot]);
  };

  const releaseBot = (botId) => {
    // Remove bot from enlistedBots
    setEnlistedBots(enlistedBots.filter((bot) => bot.id !== botId));
  };

  const dischargeBot = (botId) => {
    // Delete bot from backend and update enlistedBots
    fetch(`http://localhost:3001/bots/${botId}`, {
      method: 'DELETE',
    }).then(() => {
      setEnlistedBots(enlistedBots.filter((bot) => bot.id !== botId));
    });
  };

  const handleSort = (criteria) => {
    // Sort bots based on the selected criteria
    const sortedBots = [...bots].sort((a, b) => a[criteria] - b[criteria]);
    setBots(sortedBots);
    setSortedBy(criteria);
  };

  return (
    <Router>
      <div className="App">
        <SortBar onSort={handleSort} />
        <Route>
          <Route path="/bot-specs/:id">
            <BotSpecs bots={bots} enlistBot={enlistBot} dischargeBot={dischargeBot} />
          </Route>
          <Route path="/your-bot-army">
            <YourBotArmy enlistedBots={enlistedBots} releaseBot={releaseBot} />
          </Route>
          <Route path="/">
            <BotCollection bots={bots} enlistBot={enlistBot} />
          </Route>
        </Route>
      </div>
    </Router>
  );
};

export default App;
