import React, { useState, useEffect } from 'react';
import BotSpecs from './BotSpecs';
import SortBar from './SortBar';

const BotCollection = ({ addToArmy, filterByClass, showBotSpecs }) => {
  const [bots, setBots] = useState([]);
  const [selectedBot, setSelectedBot] = useState(null);
  const [sortProperty, setSortProperty] = useState('health');

  useEffect(() => {
    const fetchData = async () => {
      const response = fetch('http://localhost:3001/bots');
      const data = response.json();
      setBots(data);
    };

    fetchData();
  }, []);

  const sortBots = (property) => {
    const sortedBots = [...bots].sort((a, b) => a[property] - b[property]);
    setBots(sortedBots);
  };

  useEffect(() => {
    sortBots(sortProperty);
  }, [sortProperty]);

  const handleEnlist = (bot) => {
    addToArmy(bot);
    setSelectedBot(null);
  };

  const handleFilterByClass = (botClass) => {
    filterByClass(botClass);
    setSelectedBot(null);
  };

  return (
    <div>
      {selectedBot ? (
        <BotSpecs bot={selectedBot} goBack={() => setSelectedBot(null)} enlistBot={handleEnlist} />
      ) : (
        <div>
          <h2>Bot Collection</h2>
          <SortBar onSort={setSortProperty} />
          <ul>
            {bots.map(bot => (
              <li key={bot.id}>
                <button onClick={() => showBotSpecs(bot)}>Show Specs</button>
                {bot.name} - {bot.bot_class}
                <button onClick={() => handleEnlist(bot)}>Enlist</button>
                <button onClick={() => handleFilterByClass(bot.bot_class)}>Filter by Class</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default BotCollection;
