import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';
import BotSpecs from './components/BotSpecs';

const App = () => {
  const [bots, setBots] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);

  useEffect(() => {
    // Fetch data from the local server
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => setBots(data));
  }, []);

  const enlistBot = (bot) => {
    // Add bot to enlistedBots
    setEnlistedBots([...enlistedBots, bot]);
  };

  const releaseBot = (botId) => {
    // Remove bot from enlistedBots
    setEnlistedBots(enlistedBots.filter((bot) => bot.id !== botId));
  };

  const dischargeBot = (botId) => {
    // Delete bot from backend and update enlistedBots
    fetch(`http://localhost:8001/bots/${botId}`, {
      method: 'DELETE',
    }).then(() => {
      setEnlistedBots(enlistedBots.filter((bot) => bot.id !== botId));
    });
  };

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route path="/bot-specs/:id">
            <BotSpecs
              bots={bots}
              enlistBot={enlistBot}
              dischargeBot={dischargeBot}
            />
          </Route>
          <Route path="/your-bot-army">
            <YourBotArmy enlistedBots={enlistedBots} releaseBot={releaseBot} />
          </Route>
          <Route path="/">
            <BotCollection bots={bots} enlistBot={enlistBot} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
