import React from 'react';

const BotCard = ({ bot, selectBot, addBotToArmy }) => {
  const handleSelectBot = () => {
    selectBot(bot);
  };

  const handleAddToArmy = () => {
    addBotToArmy(bot);
  };

  return (
    <div className="bot-card">
      <img src={bot.avatar_url} alt={bot.name} />
      <h3>{bot.name}</h3>
      <p>{bot.bot_class}</p>
      <button className="button" onClick={handleSelectBot}>
        View Details
      </button>
      <button className="button" onClick={handleAddToArmy}>
        Enlist
      </button>
    </div>
  );
};

export default BotCard;
