import React, { useState } from 'react';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/BotArmy';

function App() {
  const [army, setArmy] = useState([]);

  const addToArmy = (bot) => {
    if (!army.find(b => b.id === bot.id)) {
      setArmy([...army, bot]);
    }
  };

  const releaseFromArmy = (bot) => {
    const updatedArmy = army.filter(b => b.id !== bot.id);
    setArmy(updatedArmy);
  };

  const dischargeBot = (botId) => 

    fetch(`http://localhost:3001/bots/${botId}`, {
      method: 'DELETE'
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Failed to delete bot with ID ${botId}`);
        }
        // Update the state to remove the bot from the army
        const updatedArmy = army.filter(b => b.id !== botId);
        setArmy(updatedArmy);
      })
      .catch(error => {
        console.error(error);
      });
  ;

  return (
    <div>
      <h1>Bot Battlr</h1>
      <BotCollection addToArmy={addToArmy} />
      <YourBotArmy army={army} releaseFromArmy={releaseFromArmy} dischargeBot={dischargeBot} />
    </div>
  );
}

export default App;
