import React, { useState, useEffect } from 'react';

const YourBotArmy = ({ removeBotFromArmy }) => {
  const [enlistedBots, setEnlistedBots] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/enlisted')
      .then((response) => response.json())
      .then((data) => setEnlistedBots(data))
      .catch((error) => console.error('Error fetching enlisted bots:', error));
  }, []);

  const handleRemoveFromArmy = (bot) => {
    // Disable the button and update text and background color
    const enlistButton = document.getElementById(`enlistButton_${bot.id}`);
    if (enlistButton) {
      enlistButton.innerText = 'Your Soldier';
      enlistButton.disabled = true;
      enlistButton.style.backgroundColor = 'red';
    }

    // Call the removeBotFromArmy function
    removeBotFromArmy(bot.id);
  };

  return (
    <div>
      <h2>Your Bot Army</h2>
      <div className="enlisted-bot">
        {enlistedBots.map((bot) => (
          <div key={bot.id} className="enlisted-bot">
            <img src={bot.avatar_url} alt={bot.name} />
            <h3>{bot.name}</h3>
            <p>Class: {bot.bot_class}</p>
            <p>Health: {bot.health}</p>
            <p>Damage: {bot.damage}</p>
            <p>Armor: {bot.armor}</p>
            <button
              id={`enlistButton_${bot.id}`}
              onClick={() => handleRemoveFromArmy(bot)}
              disabled={true} // Initially disabled
              style={{ backgroundColor: 'red' }}
            >
              Your Soldier
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YourBotArmy;
